#=======================================================================
# This file is used by FractalTree.py to create 
# a vtu mesh for linear elements (VTK_LINE)

# Created by Vijay Vedula - 2021/06/19
#=======================================================================

import vtk

#----------------------------------------------------------------------
def writeLineVTU(ptsArr, celArr, fname):

    npts = len(ptsArr)
    ncel = len(celArr)

    points = vtk.vtkPoints()
    for i in range(0, npts):
        points.InsertPoint(i, ptsArr[i])

    msh = vtk.vtkUnstructuredGrid()
    msh.Allocate(ncel)

    for i in range(0, ncel):
        msh.InsertNextCell(vtk.VTK_LINE, 2, celArr[i])

    msh.SetPoints(points)

    mshWrite = vtk.vtkXMLUnstructuredGridWriter()
    mshWrite.SetInputData(msh)
    mshWrite.SetFileName(fname)
    mshWrite.Write()

    return
